<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Files_model extends CI_Model{    public $_table = 'TbFiles';    public $primary_key = 'FileId'; // you MUST mention the primary key    protected $return_type = 'array';    public $protected = array('FileId', 'CreatedAt'); // ...Or you can set an array with the fields that cannot be filled by insert/update    function __construct()    {        parent::__construct();        $this->lang = $this->session->userdata("lang");    }    /*  This function get all news in database sort by order asc.*/    public function front()    {        $this->db->where('status', 1);        return $this;    }    /* This function get all files from database */    function create_files($file)    {        $this->db->set('FileName',$file);        $this->db->set('FileDesc', $this->input->post('FileDesc'));        $this->db->set('status', $this->input->post('status'));        $this->db->set('CreatedAt', time());        $this->db->insert('tbfiles');        $id = $this->db->insert_id();        return $id;    }    function update_files($FileId,$file)    {        $this->db->where('FileId', $FileId);        $this->db->set('FileName',$file);        $this->db->set('FileDesc', $this->input->post('FileDesc'));        $this->db->set('status', $this->input->post('status'));        $this->db->update('tbfiles');        return TRUE;    }    function delete($id) {        $this->db->where('FileId', $id);        $this->db->delete('tbfiles');        return TRUE;    }    function get_all()    {        $this->db->select("$this->_table.*")            ->from($this->_table);        return $result = $this->db->get()->result_array();    }    function get($id) {        return $this->db->query("SELECT *                                 FROM tbfiles                                WHERE FileId='$id'")->row();    }}